export default {
	isCurrentPage: (page) => {
		if(appsmith.store.page === page) return true;
		return false;
	},

	setCurrentPage: (page) => {
		storeValue('page', page);
		navigateTo(page);
	},

	downloadFile: (fileUid) => {
		storeValue('fileUid', fileUid);
		download_file.run();
		storeValue('slack_alert_activity', 'Download File From Dashboard');
		storeValue('slack_alert_description', 'Downloaded file-uid : `' + fileUid + '`');
		post_to_slack.run();
		storeValue('fileUid', undefined);
	},

	deleteSelectedFile: (fileUid) => {
		showAlert(fileUid);
		if (tbl_files.triggeredRow.deletable) {
			storeValue('deleteFileUid', fileUid);
			delete_file.run();
			storeValue('slack_alert_activity', 'Delete File From Dashboard');
			storeValue('slack_alert_description', 'Deleted file-uid : ' + fileUid);
			//storeValue('slack_alert_message', "Activity: " + appsmith.store.slack_alert_activity + "\nUsername: "+ appsmith.store.username + "\nDescription: "+ appsmith.store.slack_alert_description + ".");
			post_to_slack.run();

			storeValue('deleteFileUid', undefined);
			closeModal('model_confirm_delete');
		} else {
			showAlert('Selected file is not deletable.', 'info');
		}
	}
}
