actionConfiguration.body:
() => {
  if (tbl_bank_transaction.selectedRows.filter(function (d) {
    return d.updatable;
  }).length != 0) {
    bank_manual_recon.run();
    showAlert('Received manual recon request for ' + tbl_bank_transaction.selectedRows.length + ' transactions.', 'success');
    closeModal('b_manual_recon');
  } else {
    showAlert('Selected transactions are not updatable.', 'info');
  }
}
