export default {
	isCurrentPage: (page) => {
		if(appsmith.store.page === page) return true;
		return false;
	},

	setCurrentPage: (page) => {
		storeValue('page', page);
		navigateTo(page);
	},

	fetchNucleiTransactions: (datatype) => {
		storeValue('dataType', datatype);
		switch(Tabs1.selectedTab) {
			case 'Bank Transactions':
				try {
					fetch_bank_transactions.run();
				} catch(e) {
					return [];
				}
				break;
			case 'Nuclei Transactions':
				try {
					fetch_merchant_transactions.run();
				} catch(e) {
					return [];
				}
				break;
		}
	},

	// called to get type at the time of tab selection
	fetchTransactionType: () => {
		switch(Tabs1.selectedTab) {
			case 'Bank Transactions':
				storeValue('dataType', 'BANK_PAYMENT_DATE_TIME');
				break;
			case 'Nuclei Transactions':
				storeValue('dataType', 'NUCLEI_PAYMENT_DATE_TIME');
				break;
			case 'Merchant Transactions':
				storeValue('dataType', 'MERCHANT_PAYMENT_DATE_TIME');
				break;
		}
	}
}
